// Collect all incoming items
const allInputs = $input.all();

let documents = [];

if (allInputs.length === 1) {
  const payload = allInputs[0].json;

  if (Array.isArray(payload)) {
    // New shape: top-level array
    documents = payload;
  } else if (Array.isArray(payload.data)) {
    // Legacy shape: { data: [...] }
    documents = payload.data;
  } else if (payload.document_data || payload.docType || payload.docId) {
    // Single document already
    documents = [payload];
  } else {
    throw new Error("Unrecognized input format. Expected array of docs, object with .data array, or document object.");
  }
} else {
  // Multiple input items: assume each is already a document
  documents = allInputs.map(item => item.json);
}

// Emit each document as its own item
return documents.map((doc, index) => ({
  json: doc,
  pairedItem: { item: index },
}));

// Runs once per item
const inputDoc = $json;

// Support both shapes just in case
const doc = inputDoc.document_data ?? {};

// Keep existing file name if provided, otherwise build a safe default
const fileName =
  inputDoc.file_name ??
  `${(doc.simulationId || 'UnknownSimulation').replace(/\s+/g, '_')}/` +
  `${(doc.docType || 'UnknownType').replace(/\s+/g, '_')}_` +
  `${doc.docId || 'UnknownDocID'}.txt`;

// Preserve the entire document; then guarantee common fields exist.
// Also normalize `_metadata`/`_errors` to friendly keys if missing.
const content = {
  ...doc, // keeps file_type, company, branding, render_hints, watermark, footer, _metadata, _errors, etc.
  docType: doc.docType ?? doc.type ?? 'UnknownType',
  docId: doc.docId ?? 'UnknownDocID',
  simulationId: doc.simulationId ?? inputDoc.simulationId,
};

// Provide aliases without overwriting if the canonical keys already exist
if (doc._metadata !== undefined && content.metadata === undefined) {
  content.metadata = doc._metadata;
}
if (doc._errors !== undefined && content.errors === undefined) {
  content.errors = doc._errors;
}
// If errorSummary was kept outside the doc upstream, preserve it too
if (inputDoc.errorSummary && content.errorSummary === undefined) {
  content.errorSummary = inputDoc.errorSummary;
}

return { fileName, content };

// Runs once per item and returns HTML as binary for PDF conversion.
const doc = $json;

// ---------- guard ----------
if (!doc || !doc.content) {
  const errorHtml = `<!DOCTYPE html><html><head><meta charset="UTF-8"><title>No Data</title></head>
  <body style="font-family: Arial, sans-serif; padding: 24px;">
    <h1>No document content found</h1>
    <p>Please check the input to this node.</p>
  </body></html>`;
  return {
    json: { error: 'No document content found.' },
    binary: { data: { data: Buffer.from(errorHtml,'utf8').toString('base64'), mimeType: 'text/html', fileName: 'error_report.html' } }
  };
}

// ---------- shorthands ----------
const c = doc.content || {};
const data = c.data || {};
const company = c.company || {};
const branding = company.branding || {};
const hints = c.render_hints || {};
const currency = (data.Currency_Unit || 'USD').toString();

// ---------- theme ----------
const primary = branding.primary_color || '#1A237E';
const secondary = branding.secondary_color || '#3B82F6';
const fontFamily = branding.font_family || "Inter, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif";
const accent = (hints.header?.accent_color) || primary;
const logoSrc = branding.logo_data_uri || branding.logo_url || company.logo_url || '';

// ---------- helpers ----------
function esc(x) {
  if (x === null || x === undefined) return '';
  return String(x)
    .replace(/&/g,'&amp;')
    .replace(/</g,'&lt;')
    .replace(/>/g,'&gt;')
    .replace(/"/g,'&quot;')
    .replace(/'/g,'&#39;');
}

function isLikelyImageUrl(u='') {
  if (!u) return false;
  if (/^data:image\/(png|jpe?g|gif|svg\+xml);base64,/i.test(u)) return true;     // data URI
  return /^https?:\/\/.+\.(png|jpe?g|gif|svg)(\?.*)?$/i.test(u);
}

function asList(v) {
  if (Array.isArray(v)) return v.map(x => String(x).trim());
  if (typeof v === 'string') return v.split(';').map(x => x.trim()).filter(Boolean);
  if (v === undefined || v === null) return [];
  return [String(v)];
}

function n(x) {
  if (x === null || x === undefined || x === '') return undefined;
  const num = Number(String(x).replace(/,/g,'').trim());
  return Number.isFinite(num) ? num : undefined;
}

function money(x) {
  const val = n(x);
  if (val === undefined) return esc(x);
  try {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency }).format(val);
  } catch {
    return `${currency} ${val.toLocaleString('en-US')}`;
  }
}

function fieldChanged(field) {
  const arr = c.metadata?.errorsApplied || c._metadata?.errorsApplied || [];
  return arr.find(e => e.field === field);
}

function markedCell(field, valueHtml) {
  const e = fieldChanged(field);
  if (!e) return valueHtml;
  const title = esc(`Original: ${e.originalValue} | Error: ${e.spectrum} - ${e.reason}`);
  return `<span style="background:#fff1f1;border-left:3px solid #d9534f;padding:2px 4px;font-weight:600;" title="${title}">${valueHtml}</span>`;
}

function section(title, inner) {
  return `
  <div style="background:#fff;border:1px solid #e5e7eb;border-radius:10px;margin:16px 0;padding:18px;box-shadow:0 2px 6px rgba(0,0,0,.04);">
    <h3 style="margin:0 0 10px 0;color:${primary}">${esc(title)}</h3>
    ${inner}
  </div>`;
}

function titleFromType() {
  const t = (c.docType || '').toLowerCase();
  if (t.includes('invoice') && t.includes('services')) return 'Invoice (Services)';
  if (t.includes('invoice')) return 'Invoice';
  if (t.includes('order_document_sales_and_purchases') || t.includes('purchase order') || t.includes('(po)')) return 'Purchase Order';
  if (t.includes('shipping') && t.includes('receiv')) return 'Goods Received Note';
  if (t.includes('shipping') && (t.includes('send') || t.includes('dispatch'))) return 'Delivery Note';
  if (t.includes('bank_statement') || t.includes('bank statement')) return 'Bank Statement';
  if (t.includes('wire')) return 'Wire Transfer Confirmation';
  if (t.includes('contract')) return 'Purchase Contract';
  return hints.header?.title || 'Document';
}

function normalizeType() {
  const t = (c.docType || '').toLowerCase();
  if (t.includes('order_document_sales_and_purchases') || t.includes('purchase order') || t.includes('(po)')) return 'po';
  if (t.includes('invoice') && t.includes('services')) return 'invoice_services';
  if (t.includes('invoice')) return 'invoice_goods';
  if (t.includes('bank_statement') || t.includes('bank statement')) return 'bank';
  if (t.includes('shipping') && t.includes('receiv')) return 'ship_recv';
  if (t.includes('shipping')) return 'ship_send';
  if (t.includes('wire')) return 'wire';
  if (t.includes('contract')) return 'contract';
  return 'generic';
}

// Header with logo + address
function headerBlock() {
  const showLogo = hints.header?.show_logo !== false; // default true
  const docTitle = hints.header?.title || titleFromType();

  const logoHtml =
    (showLogo && isLikelyImageUrl(logoSrc))
      ? `<img src="${esc(logoSrc)}" alt="Logo" referrerpolicy="no-referrer"
           style="max-height:56px;object-fit:contain" onerror="this.style.display='none'">`
      : `<div style="font-weight:700;font-size:22px;">${esc(company.name || '')}</div>`;

  const addr = hints.address_block || {};
  return `
  <table style="width:100%; border-collapse:collapse;">
    <tr>
      <td style="width:60%;vertical-align:top;">
        ${logoHtml}
        <div style="font-size:12px;color:#444;margin-top:6px;">
          ${esc(addr.company_name || company.name || '')}<br>
          ${esc(addr.address || '')}${addr.address ? '<br>' : ''}${esc(addr.region || company.region || '')}
        </div>
      </td>
      <td style="text-align:right;vertical-align:top;">
