}

function footerBlock() {
  const f = hints.footer || {};
  const prepared = f.prepared_on ? new Date(f.prepared_on) : undefined;
  const dateStr = prepared && !isNaN(prepared) ? prepared.toISOString().slice(0,10) : (f.prepared_on || '');
  return `
  <div style="display:flex;justify-content:space-between;align-items:center;font-size:12px;color:#666;">
    <div>${f.prepared_by ? `Prepared by: <strong>${esc(f.prepared_by)}</strong>` : ''}</div>
    <div>${dateStr ? `Prepared on: <strong>${esc(dateStr)}</strong>` : ''}</div>
    ${hints.footer?.page_numbers ? `<div>Page <span class="pageNumber"></span></div>` : ''}
  </div>`;
}

// Print-safe, centered watermark with no wrapping
function watermarkCss() {
  const text = hints.watermark || '';
  if (!text) return '';
  const size = Number(hints.watermark_size_px ?? 48);
  const opacity = Number(hints.watermark_opacity ?? 0.06);
  return `
  <div style="
    position: fixed;
    top: 50%; left: 50%;
    transform: translate(-50%, -50%) rotate(-30deg);
    pointer-events: none; z-index: 0; opacity: ${opacity};
  ">
    <div style="
      font-size: ${size}px; font-weight: 900; letter-spacing: 2px;
      white-space: nowrap; color: ${primary};
    ">${esc(text)}</div>
  </div>
  <style>
    @media print { .container { position: relative; z-index: 1; } }
  </style>`;
}

// ---------- line items for PO/Invoice ----------
function buildLineItems() {
  const desc = asList(data.Product_Description || data.Description || data.Item_Description);
  const qty = asList(data.Item_Quantity);
  const unit = asList(data.Unit_of_measurement || data.Unit || data.UOM);
  const price = asList(data.Item_Price || data.Unit_Price);
  const maxLen = Math.max(desc.length, qty.length, unit.length, price.length);
  const rows = [];
  for (let i = 0; i < maxLen; i++) {
    const qn = n(qty[i]);
    const pr = n(price[i]);
    const lineTotal = (qn !== undefined && pr !== undefined) ? qn * pr : undefined;
    rows.push({
      description: desc[i] ?? '',
      quantity: qn ?? (qty[i] ?? ''),
      unit: unit[i] ?? '',
      unitPrice: pr ?? (price[i] ?? ''),
      lineTotal
    });
  }
  return rows.filter(r => (r.description || r.quantity || r.unitPrice));
}

function renderItemsTable() {
  const items = buildLineItems();
  if (!items.length) return '<div style="color:#666">No line items present.</div>';
  const rows = items.map((r, idx) => `
    <tr style="background:${idx%2? '#fafafa':'transparent'}">
      <td style="border:1px solid #e5e7eb;padding:10px">${esc(r.description)}</td>
      <td style="border:1px solid #e5e7eb;padding:10px;text-align:right">${esc(r.quantity)}</td>
      <td style="border:1px solid #e5e7eb;padding:10px">${esc(r.unit)}</td>
      <td style="border:1px solid #e5e7eb;padding:10px;text-align:right">${money(r.unitPrice)}</td>
      <td style="border:1px solid #e5e7eb;padding:10px;text-align:right">${r.lineTotal !== undefined ? money(r.lineTotal) : ''}</td>
    </tr>`).join('');
  const subtotal = items.reduce((s,r)=> s + (r.lineTotal || 0), 0);
  return `
  <table style="width:100%;border-collapse:collapse">
    <thead>
      <tr style="background:${secondary};color:#fff">
        <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Description</th>
        <th style="border:1px solid #e5e7eb;padding:10px;text-align:right">Qty</th>
        <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Unit</th>
        <th style="border:1px solid #e5e7eb;padding:10px;text-align:right">Unit Price</th>
        <th style="border:1px solid #e5e7eb;padding:10px;text-align:right">Line Total</th>
      </tr>
    </thead>
    <tbody>${rows}</tbody>
    <tfoot>
      <tr>
        <td colspan="4" style="border:1px solid #e5e7eb;padding:10px;text-align:right;font-weight:700">Subtotal</td>
        <td style="border:1px solid #e5e7eb;padding:10px;text-align:right;font-weight:700">${money(subtotal)}</td>
      </tr>
    </tfoot>
  </table>`;
}

// ---------- type-specific renderers ----------
function renderPO() {
  const factsRows = [
    ['PO Number', markedCell('Purchase_Order_Number', esc(data.Purchase_Order_Number || c.docId || ''))],
    ['PO Date', markedCell('Purchase_Order_Date', esc(data.Purchase_Order_Date || ''))],
