function renderBank() {
  const txnIds = asList(data.Transaction_Number);
  const txnDates = asList(data.Date_of_transaction);
  const detailsList = asList(data.Transactional_Details);
  const amountsFromDetails = detailsList.map(d => {
    const m = /Amount:\s*([0-9\.,]+)/i.exec(d || '');
    return m ? n(m[1]) : undefined;
  });

  const rows = [];
  const len = Math.max(txnIds.length, txnDates.length, detailsList.length);
  for (let i=0;i<len;i++){
    rows.push({
      id: txnIds[i] || '',
      date: txnDates[i] || '',
      details: detailsList[i] || '',
      amount: amountsFromDetails[i]
    });
  }

  const ledgerRows = rows.map((r,i)=> `
    <tr style="background:${i%2? '#fafafa':'transparent'}">
      <td style="border:1px solid #e5e7eb;padding:8px">${esc(r.date)}</td>
      <td style="border:1px solid #e5e7eb;padding:8px">${esc(r.id)}</td>
      <td style="border:1px solid #e5e7eb;padding:8px">${esc(r.details)}</td>
      <td style="border:1px solid #e5e7eb;padding:8px;text-align:right">${r.amount !== undefined ? money(r.amount) : ''}</td>
    </tr>`).join('');

  const factsRows = [
    ['Account Number', esc(data.Account_Number || '')],
    ['Statement Period', esc(data.Statement_Period || '')],
    ['Opening Balance', markedCell('Opening_or_Beginning_or_Previous_Balance', money(data.Opening_or_Beginning_or_Previous_Balance || 0))],
    ['Withdrawal/Debit Totals', markedCell('Withdrawal_or_Debit_Totals', money(data.Withdrawal_or_Debit_Totals || 0))],
