    ['Bank Name', esc(data.Bank_name || '')]
  ].map((r, i)=> `<tr style="background:${i%2? '#fafafa':'transparent'}"><td style="border:1px solid #e5e7eb;padding:8px">${r[0]}</td><td style="border:1px solid #e5e7eb;padding:8px">${r[1]}</td></tr>`).join('');

  return [
    section('Statement Summary', `<table style="width:100%;border-collapse:collapse">${factsRows}</table>`),
    section('Transactions', `
      <table style="width:100%;border-collapse:collapse">
        <thead>
          <tr style="background:${secondary};color:#fff">
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Date</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Transaction #</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Details</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:right">Amount</th>
          </tr>
