// Errors appendix (applied + cross-doc)
function renderErrorsAppendix() {
  let errorsHtml = '';
  const applied = c.metadata?.errorsApplied || c._metadata?.errorsApplied || [];
  if (applied.length) {
    const rows = applied.map((e,i)=> `
      <tr style="background:${i%2? '#fafafa':'transparent'}">
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(e.field)}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(e.spectrum)}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(e.originalValue ?? '')}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(e.errorValue ?? '')}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(e.reason)}</td>
      </tr>`).join('');
    errorsHtml += section('Errors Applied', `
      <table style="width:100%;border-collapse:collapse">
        <thead>
          <tr style="background:${secondary};color:#fff">
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Field</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Type</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Original</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Error Value</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Reason</th>
          </tr>
        </thead>
        <tbody>${rows}</tbody>
      </table>`);
  }

  const cross = c.errorSummary?.crossDocumentErrors || [];
  if (cross.length) {
    const rows = cross.map((e,i)=> `
      <tr style="background:${i%2? '#fafafa':'transparent'}">
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(e.description)}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${esc((e.affectedDocuments || []).join(', '))}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${esc((e.fields || []).join(', '))}</td>
      </tr>`).join('');
    errorsHtml += section('Cross-Document Errors', `
      <table style="width:100%;border-collapse:collapse">
        <thead>
          <tr style="background:${secondary};color:#fff">
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Description</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Affected Docs</th>
            <th style="border:1px solid #e5e7eb;padding:10px;text-align:left">Fields</th>
          </tr>
        </thead>
        <tbody>${rows}</tbody>
      </table>`);
  }

  return errorsHtml;
}

// ---------- choose template ----------
let main;
switch (normalizeType()) {
  case 'po':               main = renderPO(); break;
  case 'invoice_services': main = renderInvoice('services'); break;
  case 'invoice_goods':    main = renderInvoice('goods'); break;
  case 'bank':             main = renderBank(); break;
  case 'ship_recv':        main = renderShipping('recv'); break;
  case 'ship_send':        main = renderShipping('send'); break;
  case 'wire':             main = renderWire(); break;
  case 'contract':         main = renderContract(); break;
  default: {
    const rows = Object.entries(data).map(([k,v],i)=>`
      <tr style="background:${i%2? '#fafafa':'transparent'}">
        <td style="border:1px solid #e5e7eb;padding:8px">${esc(k)}</td>
        <td style="border:1px solid #e5e7eb;padding:8px">${markedCell(k, esc(v))}</td>
      </tr>`).join('');
    main = section('Fields', `<table style="width:100%;border-collapse:collapse">${rows}</table>`);
  }
}

const appendix = renderErrorsAppendix();

// ---------- final HTML ----------
const html = `<!DOCTYPE html>
<html style="-webkit-print-color-adjust:exact;color-adjust:exact;">
<head>
  <meta charset="UTF-8">
  <title>${esc(titleFromType())} - ${esc(c.docId || '')}</title>
  <style>
    @page { margin: 18mm; }
    body { font-family:${fontFamily}; margin:0; padding:0; background:#f6f7fb; color:#111827; }
    .container { max-width:900px; margin:0 auto; padding:24px; position:relative; z-index:1; }
    .card { background:#fff; border:1px solid #e5e7eb; border-radius:12px; padding:18px; box-shadow:0 8px 20px rgba(0,0,0,.04); }
    .brand-bar { height:6px; background:${primary}; border-radius:12px 12px 0 0; }
  </style>
</head>
<body>
  ${watermarkCss()}
  <div class="container">
    <div class="card">
      <div class="brand-bar"></div>
      ${headerBlock()}
      ${section('Parties & Roles', `
        <table style="width:100%;border-collapse:collapse">
          <tr style="background:#fafafa">
            <td style="border:1px solid #e5e7eb;padding:8px">Issuer Role</td>
            <td style="border:1px solid #e5e7eb;padding:8px">${esc(hints.issuer_role || '')}</td>
          </tr>
          <tr>
            <td style="border:1px solid #e5e7eb;padding:8px">Counterparty Role</td>
            <td style="border:1px solid #e5e7eb;padding:8px">${esc(hints.counterparty_role || '')}</td>
          </tr>
        </table>
      `)}
      ${main}
      ${appendix}
      <hr style="border:none;border-top:1px solid #e5e7eb;margin:18px 0">
      ${footerBlock()}
    </div>
  </div>
</body>
</html>`;

// ---------- output binary ----------
const outName = (doc.fileName || `${(c.simulationId||'Sim').replace(/\s+/g,'_')}_${(c.docType||'Document').replace(/\s+/g,'_')}_${c.docId||'ID'}.txt`)
  .replace(/\//g,'_')
  .replace(/\.txt$/i,'.html');

return {
  json: {},
  binary: {
    data: {
      data: Buffer.from(html, 'utf8').toString('base64'),
      mimeType: 'text/html',
      fileName: outName
    }
  }
};

// Helper to parse title like "disbursement_listing - DL-901"
function parseTitle(title = "") {
  const [rawType = "", rawId = ""] = title.split(" - ").map(s => s.trim());
  return { docType: rawType, docId: rawId };
}

const flattened = items.map(i => {
  // Drill into the nested body where the document actually is
  const inner = i?.json?.body?.body || {};
  const { format = "", title = "", data = {} } = inner;
  const { docType, docId } = parseTitle(title);

  return {
    json: {
      DocType: docType,
      DocId: docId,
      Format: format,
      ...data,
    },
  };
});

return flattened;

