// Get the input data
const data = $input.all()[0].json;

// Calculate total document count across all simulation runs
let totalDocumentCount = 0;

// Loop through each simulation run and sum up the document counts
data.simulatedResponse.simulationRuns.forEach(run => {
    totalDocumentCount += run.generatedDocuments.length;
});

// Return the result
return [{
    json: {
        ...data,
        document_count: totalDocumentCount
    }
}];

